INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (53882, 'Diflunisal', 'Diclofenac', 'Moderate', 'Concomitant use of more than one nonsteroidal anti-inflammatory drug (NSAID) at a time may increase the potential for serious gastrointestinal toxicity including inflammation, bleeding, ulceration, and perforation of the esophagus, stomach, or intestines. These events can occur at any time during NSAID use, with or without warning symptoms. The risk is dependent on both dosage and duration of therapy.', 'DDInter', 'Concomitant use of more than one NSAID at a time should generally be avoided. Patients treated with an NSAID should be advised to take it with food and to immediately report signs and symptoms of GI ulceration and bleeding such as severe abdominal pain, dizziness, lightheadedness, and the appearance of black, tarry stools. The selective use of prophylactic anti-ulcer therapy (e.g., antacids, misoprostol, proton pump inhibitors) may be considered in high risk patients.', 'Synergism', 'Concomitant use of more than one NSAID at a time should generally be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53953/', '', 'Fluocinolone acetonide, Fluorometholone, Fluocinolone acetonide, Salicylic acid, Fluorometholone, Rabeprazole, Misoprostol, Chondroitin sulfate, Glucosamine, Esomeprazole, Tolazoline, More', 'Salicylic acid, Choline salicylate, Ziconotide, Salsalate', 1767369485);
