INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (53884, 'Diltiazem', 'Diclofenac', 'Moderate', 'Limited data indicate that some cyclooxygenase inhibitors may attenuate the antihypertensive effects of some calcium channel blockers. The mechanism appears to be related to an alteration of vascular tone, which is dependent on prostacyclins and other vasodilatory prostanoids. When a nonsteroidal anti-inflammatory drug (NSAID) is added to the regimen of a patient who is already taking a calcium channel blocker, increased blood pressure may result. Also, the clinician should be aware that the risk of hypotension is increased when NSAIDs are withdrawn from the regimen.', 'DDInter', 'Monitoring for altered blood pressure control is recommended.', 'Antagonism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/53955/', '', 'Tetracaine, Zinc sulfate, Zinc acetate, Fluocinolone acetonide, Fluocinonide, Zinc chloride, Procaine, Fluorometholone, Zinc gluconate', 'Fluocinolone acetonide, Fluorometholone, Fluocinolone acetonide, Fluorometholone, Chondroitin sulfate, Esomeprazole, Glucosamine, Tolazoline, Dimethyl sulfoxide, Deoxycholic acid, Pimecrolimus, More', 1767369485);
