INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (53980, 'Diclofenac', 'Methotrexate', 'Major', 'Coadministration with nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the plasma concentrations and toxicities of methotrexate. The proposed mechanism is NSAID inhibition of the renal elimination of methotrexate and its metabolite, 7-hydroxymethotrexate, although data from pharmacokinetic studies are inconsistent and conflicting. Displacement of methotrexate binding to serum albumin by certain NSAIDs may also play a secondary role. Unexpectedly severe and sometimes fatal bone marrow suppression, aplastic anemia, gastrointestinal toxicity, and nephrotoxicity have been reported during concomitant administration of methotrexate with NSAIDs. The risk is greatest in patients receiving high dosages of methotrexate and those with renal impairment.', 'DDInter', 'NSAIDs should generally not be administered prior to or concomitantly with high dosages of methotrexate, such as those used to treat osteosarcoma. Caution should be exercised when NSAIDs are administered concomitantly with lower dosages of methotrexate. Close monitoring for signs and symptoms of bone marrow suppression, nephrotoxicity, and hepatotoxicity is recommended during treatment. Patients should be advised to contact their physician if they develop stomatitis, nausea, vomiting, diarrhea, rash, anorexia, jaundice, dark urine, dry cough, shortness of breath, and/or signs and symptoms of myelosuppression such as pallor, dizziness, fatigue, lethargy, fainting, easy bruising or bleeding, fever, chills, sore throat, body aches, and other influenza-like symptoms. Patients should also be counseled to avoid any other over-the-counter NSAID products.', 'Excretion', 'NSAIDs should generally not be administered prior to or concomitantly with high dosages of methotrexate, such as those used to treat osteosarcoma.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54051/', '', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Abatacept, Siponimod, More', 'Fluocinolone acetonide, Fluorometholone, Fluocinolone acetonide, Fluorometholone, Chondroitin sulfate, Glucosamine, Tolazoline, Dimethyl sulfoxide, Minoxidil, Deoxycholic acid, Calcium gluconate, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (53981, 'Diclofenac', 'Methoxyflurane', 'Moderate', 'Coadministration of methoxyflurane with other nephrotoxic agents may increase the risk of renal impairment. The precise mechanism has not been delineated.', 'DDInter', 'Concomitant use of methoxyflurane with other potentially nephrotoxic agents should generally be avoided. Patients should be advised to seek medical attention if they experience symptoms that may indicate nephrotoxicity such as decreased urine output, sudden weight gain, fluid retention, edema, or shortness of breath.', 'Synergism', 'Concomitant use of methoxyflurane with other potentially nephrotoxic agents should generally be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54052/', '', 'Fludrocortisone, Fluocinolone acetonide, Hydrocortisone, Prednisolone, Betamethasone, Fluorometholone, Methylprednisolone, Dexamethasone, Salicylic acid, Fluocinolone acetonide, Cortisone, More', 'Salicylic acid, Choline salicylate, Ziconotide, Salsalate', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (53982, 'Diclofenac', 'Methylprednisolone', 'Moderate', 'The combined use of corticosteroids and nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the potential for serious gastrointestinal (GI) toxicity, including inflammation, bleeding, ulceration, and perforation.', 'DDInter', 'Caution is advised if corticosteroids and NSAIDs are used together, especially in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly and debilitated patients. During concomitant therapy, patients should be advised to take the medications with food and to immediately report signs and symptoms of GI ulceration and bleeding such as severe abdominal pain, dizziness, lightheadedness, and the appearance of black, tarry stools. The selective use of prophylactic anti-ulcer therapy (e.g., antacids, H2-antagonists) may be considered.', 'Synergism', 'Caution is advised if corticosteroids and NSAIDs are used together, especially in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly and debilitated patients.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54053/', '', 'Fludrocortisone, Fluocinolone acetonide, Betamethasone, Fluorometholone, Methylprednisolone, Fluocinolone acetonide, Cortisone, Betamethasone, Fluorometholone, Chondroitin sulfate, Esomeprazole, More', 'Vitamin A, Isotretinoin, Fluorometholone, Dapsone, Azelaic acid, Adapalene, Fluocinolone acetonide, Beclomethasone dipropionate, Fluocinonide, Fluorometholone, Fluorometholone, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (53983, 'Diclofenac', 'Metolazone', 'Moderate', 'Concomitant use of nonsteroidal anti-inflammatory drugs (NSAIDs) and diuretics may adversely affect renal function due to NSAID inhibition of the renal synthesis of prostaglandins that help maintain renal perfusion in dehydrated states. The risk may be increased in patients on dietary sodium restriction. At the same time, hypotensive effect of the diuretics may be reduced because inhibition of prostaglandins can lead to unopposed pressor activity and, consequently, elevation in blood pressure. Natriuretic and diuretic effects may also be reduced, as NSAIDs have been reported to cause sodium and water retention, which may account for the increased risk of congestive heart failure associated with the combination. NSAIDs may also increase the risk of hyperkalemia associated with potassium-sparing diuretics.', 'DDInter', 'In patients receiving both diuretic and NSAID therapy, management consists of avoiding dehydration and carefully monitoring the patient''s renal function and blood pressure. If renal insufficiency or hyperkalemia develops, both drugs should be discontinued until the condition is corrected.', 'Antagonism', 'In patients receiving both diuretic and NSAID therapy, management consists of avoiding dehydration and carefully monitoring the patient''s renal function and blood pressure.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54054/', '', 'Tinidazole, Ascorbic acid, Simeprevir, Lactic acid, Methazolamide, Ciclopirox, Vemurafenib, Darunavir, Tioconazole, Furazolidone, Diiodohydroxyquinoline, More', 'Fluocinolone acetonide, Fluorometholone, Fluocinolone acetonide, Salicylic acid, Fluorometholone, Misoprostol, Chondroit