INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (54059, 'Human Rho(D) immune globulin', 'Diclofenac', 'Major', 'Coadministration of intravenous immune globulin preparations with nephrotoxic agents may potentiate the risk of renal impairment. Many commercially available intravenous formulations of immune globulin contain sucrose as a stabilizer.', 'DDInter', 'Intravenous immune globulin preparations should be administered cautiously in patients treated with other potentially nephrotoxic agents (e.g., e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Clinicians should ensure that patients are not volume depleted prior to the initiation of immune globulin therapy. Monitoring of urine output and renal function tests, including the measurement of blood urea nitrogen (BUN) and serum creatinine, is recommended prior to the initial infusion and at appropriate intervals thereafter.', 'Synergism', 'Intravenous immune globulin preparations should be administered cautiously in patients treated with other potentially nephrotoxic agents (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54130/', '', 'Fludrocortisone, Fluocinolone acetonide, Hydrocortisone, Prednisolone, Betamethasone, Fluorometholone, Methylprednisolone, Dexamethasone, Salicylic acid, Fluocinolone acetonide, Cortisone, More', 'Bezlotoxumab, Palivizumab', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (54060, 'Diclofenac', 'Rifampicin', 'Moderate', 'Coadministration with CYP450 2C9 inducers, such as rifampin, may decrease the plasma concentrations and therapeutic effects of diclofenac. The proposed mechanism is rifampin-mediated induction of CYP450 2C9, the isoenzyme responsible for the metabolic clearance of diclofenac.', 'DDInter', 'The potential for diminished pharmacologic effects of diclofenac should be considered during coadministration with CYP450 2C9 inducers, such as rifampin. Pharmacologic response to diclofenac should be monitored and the diclofenac dosage adjusted as necessary whenever a CYP450 2C9 inducer is added to or withdrawn from therapy.', 'Metabolism', 'The potential for diminished pharmacologic effects of diclofenac should be considered during coadministration with CYP450 2C9 inducers, such as rifampin.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54131/', '', 'Indomethacin, Fluocinolone acetonide, Fluorometholone, Salicylic acid, Ketorolac, Indomethacin, Fluocinolone acetonide, Piroxicam, Flurbiprofen, Fluorometholone, Bromfenac, More', 'Capreomycin, Pyrazinamide, Rifamycin, Sulfamethoxazole, Cycloserine, Rifabutin, Ethambutol, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (54061, 'Diclofenac', 'Rifapentine', 'Moderate', 'Coadministration with rifapentine may decrease the plasma concentrations of drugs that are substrates of the CYP450 2C8, 2C9, and/or 3A4 isoenzymes. The mechanism is accelerated clearance due to induction of these isoenzymes by rifapentine. Enzyme activities may be induced within 4 days of the first dose and return to normal 14 days after discontinuation of rifapentine.', 'DDInter', 'The possibility of a diminished therapeutic response to drugs that are known substrates of CYP450 2C8, 2C9, and/or 3A4 should be considered during coadministration with rifapentine. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs, particularly those with a narrow therapeutic range, whenever rifapentine is added to or withdrawn from therapy.', 'Metabolism', 'The possibility of a diminished therapeutic response to drugs that are known substrates of CYP450 2C8, 2C9, and/or 3A4 should be considered during coadministration with rifapentine.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54132/', '', 'Fluocinolone acetonide, Fluorometholone, Salicylic acid, Ketorolac, Fluocinolone acetonide, Fluorometholone, Bromfenac, Fenoprofen, Meclofenamic acid, Ketorolac, Phenylbutazone, More', 'Capreomycin, Pyrazinamide, Rifamycin, Sulfamethoxazole, Cycloserine, Rifabutin, Ethambutol, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid', 1767369485);
