INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (55685, 'Dexlansoprazole', 'Capreomycin', 'Moderate', 'Chronic use of proton pump inhibitors (PPIs) may induce hypomagnesemia, and the risk may be increased during concomitant use of diuretics or other agents that can cause magnesium loss. Serious adverse events include tetany, seizures, tremor, carpopedal spasm, atrial fibrillation, supraventricular tachycardia, and abnormal QT interval; however, patients do not always exhibit these symptoms. Hypomagnesemia can also cause impaired parathyroid hormone secretion, which may lead to hypocalcemia. In approximately 25% of the cases of PPI-associated hypomagnesemia reviewed by the FDA, the condition did not resolve with magnesium supplementation alone but also required discontinuation of the PPI.', 'DDInter', 'Monitoring of serum magnesium levels is recommended prior to initiation of therapy and periodically thereafter if prolonged treatment with a proton pump inhibitor is anticipated or when combined with other agents that can cause hypomagnesemia such as diuretics, aminoglycosides, cation exchange resins, amphotericin B, cetuximab, cisplatin, cyclosporine, foscarnet, panitumumab, pentamidine, and tacrolimus. Patients should be advised to seek immediate medical attention if they develop potential signs and symptoms of hypomagnesemia such as palpitations, arrhythmia, muscle spasm, tremor, or convulsions. In children, abnormal heart rates may cause fatigue, upset stomach, dizziness, and lightheadedness. Magnesium replacement as well as discontinuation of the PPI may be required in some patients.', 'Synergism', 'Monitoring of serum magnesium levels is recommended prior to initiation of therapy and periodically thereafter if prolonged treatment with a proton pump inhibitor is anticipated or when combined with other agents that can cause hypomagnesemia such as diuretics, aminoglycosides, cation exchange resins, amphotericin B, cetuximab, cisplatin, cyclosporine, foscarnet, panitumumab, pentamidine, and tacrolimus.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/55756/', '', 'Pyrazinamide, Rifamycin, Bedaquiline, Sulfamethoxazole, Rifapentine, Trimethoprim, Cycloserine, Rifabutin, Isoniazid, Ethambutol, Ethionamide, More', 'Tinidazole, Levofloxacin, Tetracycline, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Metronidazole, Famotidine, Rifabutin, Cimetidine, Nizatidine, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (55686, 'Diatrizoate', 'Capreomycin', 'Major', 'Concomitant use of intravascular radiocontrast media with other nephrotoxic agents may potentiate the risk of contrast-induced nephropathy and renal impairment. While the condition is usually transient and asymptomatic, it can be associated with increased risk of renal failure, dialysis, prolonged hospitalization, significant long-term morbidity, and mortality.', 'DDInter', 'Alternative imaging techniques that do not require contrast should be considered in patients who are at increased risk for contrast-induced nephropathy. Any repeat procedures with contrast media, if necessary, should not occur until at least 48 to 72 hours after the previous contrast exposure and renal function has fully recovered.', 'Synergism', 'Alternative imaging techniques that do not require contrast should be considered in patients who are at increased risk for contrast-induced nephropathy.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/55757/', '', 'Ethiodized oil, Iopanoic acid, Tyropanoic acid, Iopodic acid', 'Pyrazinamide, Rifamycin, Bedaquiline, Sulfamethoxazole, Rifapentine, Trimethoprim, Rifampicin, Cycloserine, Rifabutin, Isoniazid, Ethambutol, More', 1767369485);
