INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (56134, 'Aminoglutethimide', 'Carbamazepine', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations and pharmacologic effects of carbamazepine, which is primarily metabolized by the isoenzyme. The interaction has been reported with known CYP450 3A4 inducers such as phenobarbital, phenytoin, and primidone.', 'DDInter', 'Pharmacologic effects and serum concentrations of carbamazepine should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the carbamazepine dosage adjusted as necessary.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56206/', '', 'Anastrozole, Abarelix, Flutamide, Bicalutamide, Degarelix, Nilutamide', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Lamotrigine, Methylphenobarbital, Phenytoin, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, More', 1767369485);
