INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (56265, 'Erythromycin', 'Carbamazepine', 'Major', 'Some macrolide antibiotics can significantly increase serum carbamazepine levels. The mechanism is probably inhibition of hepatic CYP450 3A4 isoenzymes. Severe carbamazepine toxicity has been reported. At the same time, carbamazepine may induce the CYP450 3A4 metabolism of macrolides. This may lead to sub-therapeutic levels of macrolides and a decreased antibiotic effect.', 'DDInter', 'Monitoring of carbamazepine blood levels is recommended, and the patient should be carefully observed for signs of carbamazepine toxicity. Patients should be advised to report signs of carbamazepine toxicity (nausea, visual disturbances, dizziness, or ataxia) to their physicians. The carbamazepine dosage may require reduction. Monitor for a reduction in the antimicrobial effects of the macrolide if this combination must be used. Alternative antimicrobial therapy, if available, is generally recommended for patients on carbamazepine.', 'Metabolism', 'Monitoring of carbamazepine blood levels is recommended, and the patient should be carefully observed for signs of carbamazepine toxicity.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56337/', '', 'Lincomycin, Dirithromycin, Vitamin A, Chloramphenicol, Fluorometholone, Dapsone, Azelaic acid, Adapalene, Rifamycin, Kanamycin, Oxytetracycline, More', 'Tiagabine, Brivaracetam, Rufinamide, Methsuximide, Paramethadione, Fenfluramine, Perampanel, Eslicarbazepine, Phensuximide, Methylphenobarbital, Phenacemide, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (56266, 'Escitalopram', 'Carbamazepine', 'Minor', 'The manufacturer of citalopram has reported that concomitant administration of carbamazepine with citalopram or escitalopram (both CYP450 3A4 substrates), did not significantly affect carbamazepine pharmacokinetics or citalopram or escitalopram levels. However, due to the enzyme inducing property of carbamazepine, the potential for increased clearance of citalopram should be considered. Dosage adjustments may be necessary if an interaction is suspected.', 'DDInter', '-', 'Metabolism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56338/', '', 'Oxitriptan, Tryptophan, Milnacipran, Levomilnacipran', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (56267, 'Esketamine', 'Carbamazepine', 'Moderate', 'Concomitant use of esketamine with central nervous system (CNS) depressants may increase sedation and impairment of attention, judgment, thinking, reaction speed, and psychomotor skills.', 'DDInter', 'Caution is advised and patients should be closely monitored during concomitant use of esketamine with CNS depressants. Due to the risk of delayed or prolonged sedation and other adverse effects, patients should be monitored for at least 2 hours after esketamine administration, followed by an assessment to determine when the patient is considered clinically stable and ready to leave the healthcare setting. Patients should be instructed not to engage in potentially hazardous activities, such as driving a motor vehicle or operating machinery, until the next day after a restful sleep.', 'Synergism', 'Caution is advised and patients should be closely monitored during concomitant use of esketamine with CNS depressants.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56339/', '', 'Oxitriptan, Tryptophan, Milnacipran, Levomilnacipran, Isoflurane, Nitrous oxide, Desflurane, Enflurane, Halothane, Etomidate', '', 1767369485);
