INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (56286, 'Febuxostat', 'Carbamazepine', 'Minor', 'Potent inducers of UGT enzymes (e.g., rifampin, carbamazepine, phenobarbital, phenytoin, ritonavir) may increase the metabolism of febuxostat and decrease its therapeutic efficacy. Febuxostat undergoes extensive metabolism by both conjugation via UGT enzymes including UGT1A1, UGT1A3, UGT1A9, and UGT2B7, and oxidation via cytochrome P450 enzymes. The relative contribution of each isoenzyme and the clinical significance is unknown. Conversely, increased plasma levels of febuxostat may occur after a potent UGT inducer is discontinued.', 'DDInter', '-', 'Metabolism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56358/', '', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Lamotrigine, Methylphenobarbital, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Oxcarbazepine, More', 'Pegloticase, Colchicine, Rasburicase', 1767369485);
