INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (56489, 'Carbamazepine', 'Pirfenidone', 'Moderate', 'Coadministration with potent inducers of CYP450 1A2 or other isoenzymes involved in the metabolism of pirfenidone such as CYP450 2C9 and 2C19 may significantly decrease the plasma concentrations of pirfenidone. Loss of therapeutic efficacy may result, as pirfenidone is primarily (70% to 80%) metabolized by CYP450 1A2, with minor contribution from CYP450 2C9, 2C19, 2D6, and 2E1.', 'DDInter', 'Potent inducers of CYP450 1A2, 2C9, and/or 2C19 such as phenytoin, carbamazepine, and rifampin should be discontinued prior to the initiation of pirfenidone and avoided during therapy.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56561/', '', 'Brivaracetam, Topiramate, Ezogabine, Lamotrigine, Methylphenobarbital, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Ethotoin, Rufinamide, More', 'Antithymocyte immunoglobulin (rabbit), Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, Inebilizumab, Belimumab, Sutimlimab, Risankizumab, Antilymphocyte immunoglobulin (horse), More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (56490, 'Carbamazepine', 'Pitolisant', 'Major', 'Coadministration with potent CYP450 3A4 inducers may decrease the systemic exposure and efficacy of pitolisant. The proposed mechanism is induction of CYP450 3A4-mediated metabolism of pitolisant, which has been shown to be metabolized by this isoenzyme.', 'DDInter', 'Patients should be assessed for loss of efficacy of pitolisant following initiation of a potent CYP450 3A4 inducer, and the pitolisant dosage adjusted as necessary. For patients already receiving a stable dose of pitolisant 8.9 mg or 17.8 mg once daily, the dose of pitolisant should be increased to double the original daily dose (i.e., 17.8 mg or 35.6 mg, respectively) over 7 days. The dosage of pitolisant should be reduced by half if concomitant use of a strong CYP450 3A4 inducer is discontinued.', 'Metabolism', 'Patients should be assessed for loss of efficacy of pitolisant following initiation of a potent CYP450 3A4 inducer, and the pitolisant dosage adjusted as necessary.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56562/', '', 'Brivaracetam, Topiramate, Lamotrigine, Methylphenobarbital, Lacosamide, Levetiracetam, Oxcarbazepine, Ethotoin, Rufinamide, Clonazepam, Paramethadione, More', 'Dalfampridine, Riluzole, Inotersen, Amifampridine, Tafamidis', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (56491, 'Carbamazepine', 'Polatuzumab vedotin', 'Minor', 'Coadministration with potent inducers of CYP450 3A4 may decrease exposure to unconjugated monomethyl auristatin E (MMAE), the anti-mitotic and cytotoxic component of polatuzumab vedotin. Polatuzumab vedotin is an antibody-drug conjugate (ADC) that releases MMAE via proteolytic cleavage, and MMAE has been shown in vitro to be primarily metabolized by CYP450 3A4.', 'DDInter', '-', 'Metabolism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56563/', '', 'Brivaracetam, Topiramate, Ezogabine, Lamotrigine, Methylphenobarbital, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Oxcarbazepine, Ethotoin, More', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (56492, 'Carbamazepine', 'Polythiazide', 'Moderate', 'Carbamazepine may cause clinically significant hyponatremia. Data show that the risk of hyponatremia may be additive when carbamazepine and hydrochlorothiazide are used concurrently. Carbamazepine and other diuretics may interact in a similar manner.', 'DDInter', 'Clinical monitoring of patient mental status and periodic measurement of serum sodium concentrations is recommended. Patients should be advised to notify their caregiver if they experience possible symptoms of hyponatremia such as nausea, vomiting, lethargy, weakness, muscle cramps, twitching, confusion, or seizures.', 'Synergism', 'Clinical monitoring of patient mental status and periodic measurement of serum sodium concentrations is recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56564/', '', 'Brivaracetam, Ezogabine, Cenobamate, Lamotrigine, Methylphenobarbital, Phenytoin, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Oxcarbazepine, More', 'Tinidazole, Ascorbic acid, Oxytetracycline, Sulfamethoxazole, Ciclopirox, Sulfamethizole, Tioconazole, Diazoxide, Econazole, Sulfadiazine, Chloramphenicol, More', 1767369485);
