INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (56561, 'Carbamazepine', 'Selegiline', 'Major', 'Coadministration of monoamine oxidase inhibitors (MAOIs) and dibenzazepine derivatives (e.g., tricyclic and tetracyclic antidepressants, cyclobenzaprine, carbamazepine) may produce significant adverse reactions including nausea, vomiting, flushing, dizziness, tremor, myoclonus, rigidity, diaphoresis, hyperthermia, autonomic instability, hypertensive crises, disseminated intravascular coagulation, severe convulsive seizures, coma, and death. The exact mechanism of interaction is unknown, but may involve excessive serotonergic activity in the central nervous system (i.e., serotonin syndrome).', 'DDInter', 'In general, dibenzazepine derivatives should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, methylene blue, procarbazine). At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with tricyclic antidepressants, and vice versa.', 'Others', 'In general, dibenzazepine derivatives should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56633/', '', 'Lacosamide', '', 1767369485);
