INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (56614, 'Carbamazepine', 'Thyroid, porcine', 'Moderate', 'Carbamazepine and phenytoin may decrease exogenous thyroid plasma concentrations resulting in elevated thyroid stimulating hormone (TSH) and possible exacerbation of hypothyroidism. The mechanism may be induction of hepatic CYP450 enzymes responsible for thyroxine (T4) and triiodothyronine (T3) metabolism. In addition, T3 and T4 may be displaced from thyroxine binding globulin by phenytoin. Clinical data are limited.', 'DDInter', 'Clinical monitoring of patient response, including laboratory serum TSH concentrations, is recommended. Adjustment of the thyroid replacement dosage may be indicated when initiating or discontinuing phenytoin or carbamazepine therapy.', 'Metabolism', 'Clinical monitoring of patient response, including laboratory serum TSH concentrations, is recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56686/', '', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Lamotrigine, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Oxcarbazepine, Ethotoin, More', '', 1767369485);
