INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (57364, 'Ziprasidone', 'Levonordefrin', 'Moderate', 'Phenothiazines and other neuroleptics may inhibit or reverse the pressor effect of adrenaline (epinephrine), dopamine, and similar vasoconstrictors. Many of these agents, including the atypical antipsychotics, exhibit alpha-1 adrenergic blocking activity and produce hypotension as an adverse effect. Use of adrenaline or dopamine for drug-induced hypotension and circulatory collapse in patients receiving neuroleptic therapy may cause a paradoxical further lowering of blood pressure, since beta stimulation may worsen hypotension in the setting of alpha blockade.', 'DDInter', 'Adrenaline, dopamine, and similar vasoconstrictors should not be used to treat drug-induced hypotension and circulatory collapse in patients taking phenothiazines or other neuroleptic agents. Alternative vasopressor agents such as metaraminol, noradrenaline (norepinephrine), or phenylephrine should be considered, and vital signs closely monitored.', 'Antagonism', 'Adrenaline, dopamine, and similar vasoconstrictors should not be used to treat drug-induced hypotension and circulatory collapse in patients taking phenothiazines or other neuroleptic agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/57440/', '', 'Cariprazine, Droperidol, Pimavanserin', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (57365, 'Human adenovirus e serotype 4 strain cl-68578 antigen', 'Carboplatin', 'Moderate', 'There are no data regarding the use of live, unattenuated Adenovirus Type 4 and Type 7 vaccine during treatment with immunosuppressive therapies, including alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, and long-term topical or inhaled corticosteroids. It is not known whether immunosuppressed patients may have an increased risk of adverse reactions or a decreased or suboptimal immunologic response to the vaccine.', 'DDInter', 'The safety and efficacy of live, unattenuated Adenovirus Type 4 and Type 7 vaccine have not been established in patients receiving immunosuppressive therapies. Caution is advised.', 'Others', 'The safety and efficacy of live, unattenuated Adenovirus Type 4 and Type 7 vaccine have not been established in patients receiving immunosuppressive therapies.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/57441/', '', 'Pertuzumab, Aminolevulinic acid, Tazemetostat, Tagraxofusp, Sotorasib, Enasidenib, Belzutifan, Mitotane, Porfimer sodium, Anagrelide, Ivosidenib, More', '', 1767369485);
