INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (57696, 'Echinacea', 'Diltiazem', 'Moderate', 'Coadministration with echinacea may alter the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4. Echinacea appears to inhibit intestinal CYP450 3A4, which would lead to an increase in oral midazolam (a sensitive 3A4 substrate) bioavailability; however, plasma levels of midazolam following oral administration do not appear to be affected by echinacea. In contrast, it appears that echinacea may also induce hepatic CYP450 3A4; thereby increasing the hepatic clearance of drugs that are substrates of CYP450 3A4.', 'DDInter', 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines. If echinacea is prescribed with a drug that is a CYP450 3A4 substrate, the possibility of an altered (increased or decreased) therapeutic response should be considered. Patients should be monitored more closely following the addition or withdrawal of echinacea and the dosage of the CYP450 3A4 substrate adjusted as necessary.', 'Metabolism', 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/57772/', '', 'Tetracaine, Zinc sulfate, Zinc acetate, Fluocinolone acetonide, Fluocinonide, Isosorbide dinitrate, Zinc chloride, Procaine, Fluorometholone, Zinc gluconate, Nitroglycerin', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (57697, 'Edetate calcium disodium anhydrous', 'Diltiazem', 'Moderate', 'Calcium-containing products may decrease the effectiveness of calcium channel blockers by saturating calcium channels with calcium. Calcium chloride has been used to manage acute severe verapamil toxicity.', 'DDInter', 'Management consists of monitoring the effectiveness of calcium channel blocker therapy during coadministration with calcium products.', 'Antagonism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/57773/', '', 'Lidocaine, Zinc sulfate, Tetracaine, Zinc acetate, Fluocinolone acetonide, Hydrocortisone, Fluocinonide, Isosorbide dinitrate, Zinc chloride, Procaine, Prednisolone, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (57698, 'Edoxaban', 'Diltiazem', 'Major', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of edoxaban, which is a substrate of the efflux transporter.', 'DDInter', 'When used for the treatment of deep vein thrombosis and pulmonary embolism, the manufacturer recommends that edoxaban dosage be reduced to 30 mg once daily in patients receiving concomitant treatment with certain P-gp inhibitors including azithromycin, clarithromycin, erythromycin, oral itraconazole, oral ketoconazole, quinidine, and verapamil. This dosage recommendation is based on data from a clinical study, the Hokusai VTE study, and is limited to use with the specific P-gp inhibitors mentioned.', 'Absorption', 'When used for the treatment of deep vein thrombosis and pulmonary embolism, the manufacturer recommends that edoxaban dosage be reduced to 30 mg once daily in patients receiving concomitant treatment with certain P-gp inhibitors including azithromycin, clarithromycin, erythromycin, oral itraconazole, oral ketoconazole, quinidine, and verapamil.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/57774/', '', 'Lidocaine, Zinc sulfate, Tetracaine, Zinc acetate, Fluocinolone acetonide, Hydrocortisone, Fluocinonide, Isosorbide dinitrate, Zinc chloride, Procaine, Prednisolone, More', 'Lepirudin, Urokinase, Cangrelor, Caplacizumab, Argatroban, Prasugrel, Desirudin, Streptokinase, Tinzaparin, Vorapaxar, Enoxaparin, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (57699, 'Efavirenz', 'Diltiazem', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may significantly decrease the plasma concentrations of diltiazem, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Use of diltiazem with rifampin or any known CYP450 3A4 inducer should be avoided when possible, and alternative therapy considered. Diltiazem dose adjustment should be guided by clinical response if concomitant use with a CYP450 3A4 inducer is necessary.', 'Metabolism', 'Use of diltiazem with rifampin or any known CYP450 3A4 inducer should be avoided when possible, and alternative therapy considered.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/57775/', '', 'Tetracaine, Zinc sulfate, Zinc acetate, Fluocinolone acetonide, Fluocinonide, Isosorbide dinitrate, Zinc chloride, Procaine, Fluorometholone, Zinc gluconate, Nitroglycerin', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Velpatasvir, Brincidofovir, Stavudine, Remdesivir, Elbasvir, Voxilaprevir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (57700, 'Elagolix', 'Diltiazem', 'Moderate', 'Coadministration with inhibitors of the OATP 1B1 hepatic uptake transporter, CYP450 3A4 metabolic isoenzyme, and/or P-glycoprotein (P-gp) efflux transporter may increase the plasma concentrations of elagolix. The effect of P-gp inhibitors on the pharmacokinetics of elagolix has not been studied, but some increases in elagolix exposure may occur, as it is a P-gp substrate. Increased exposure to elagolix may increase the risk of serious adverse effects such as bone loss, suicidal ideation and behavior, exacerbation of mood disorders, and hepatic transaminase elevations.', 'DDInter', 'Caution is advised when elagolix is used with OATP 1B1, CYP450 3A4, and/or P-gp inhibitors. Patients should be monitored for potentially altered effects of elagolix following the initiation or discontinuation of these inhibitors, and the elagolix dosage adjusted as necessary.', 'Metabolism', 'Caution is advised when elagolix is used with OATP 1B1, CYP450 3A4, and/or P-gp inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/57776/', '', 'Verapamil, Tetracaine, Zinc sulfate, Zinc acetate, Fluocinolone acetonide, Hydrocortisone, Fluocinonide, Isosorbide dinitrate, Zinc chloride, Procaine, Prednisolone, More', 'Ganirelix, Cetrorelix, Nafarelin, Gonadorelin', 1767369485);
