INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (57704, 'Enalapril', 'Diltiazem', 'Minor', 'Calcium channel blockers and angiotensin converting enzyme (ACE) inhibitors may have additive hypotensive effects. While these drugs are often safely used together, careful monitoring of the systemic blood pressure is recommended during coadministration, especially during the first one to three weeks of therapy.', 'DDInter', '-', 'Synergism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/57780/', '', 'Tetracaine, Zinc sulfate, Zinc acetate, Fluocinolone acetonide, Zinc chloride, Procaine, Fluorometholone, Zinc gluconate', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (57705, 'Encorafenib', 'Diltiazem', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of encorafenib, which is primarily metabolized by the isoenzyme. Increased exposure to encorafenib may increase the risk of serious and life-threatening adverse effects such as hemorrhage, uveitis, QT prolongation, hepatotoxicity, dermatologic reactions, and new malignancies.', 'DDInter', 'Concomitant use of encorafenib with moderate CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends reducing the encorafenib dose to one-half of the dose used prior to addition of the moderate CYP450 3A4 inhibitor. After the inhibitor has been discontinued for 3 to 5 elimination half-lives, the encorafenib dose that was taken prior to initiating the inhibitor may be resumed.', 'Metabolism', 'Concomitant use of encorafenib with moderate CYP450 3A4 inhibitors should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/57781/', '', 'Lidocaine, Zinc sulfate, Tetracaine, Zinc acetate, Fluocinolone acetonide, Hydrocortisone, Fluocinonide, Isosorbide dinitrate, Zinc chloride, Procaine, Prednisolone, More', 'Regorafenib, Lenvatinib, Erdafitinib, Trametinib, Vandetanib, Dacomitinib, Tivozanib', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (57706, 'Enflurane', 'Diltiazem', 'Moderate', 'Calcium channel blockers may potentiate the cardiac depressant and hypotensive effects of volatile (halogenated) anesthetics, and vice versa, due to their similar pharmacologic action on the cardiovasculature. Serious consequences of this interaction may include cardiovascular collapse, ventricular impairment, and/or conduction disturbances, particularly in patients with underlying cardiac complications. The interaction may be more likely with verapamil and diltiazem due to their dromotropic and chronotropic effects.', 'DDInter', 'Discontinuation of calcium channel blockers prior to surgery is generally not recommended because of the rebound effect in patients with coronary heart disease or arterial hypertension. However, extreme care must be taken when administering halogenated anesthetics to patients under treatment with calcium channel blockers. Hemodynamic and electrocardiographic monitoring are recommended during the operation and postoperatively, and the dosage of one or both drugs adjusted as necessary.', 'Synergism', 'Discontinuation of calcium channel blockers prior to surgery is generally not recommended because of the rebound effect in patients with coronary heart disease or arterial hypertension.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/57782/', '', 'Thiopental, Remifentanil, Nitrous oxide, Methohexital, Esketamine, Etomidate, Ketamine', 'Lidocaine, Zinc sulfate, Tetracaine, Zinc acetate, Fluocinolone acetonide, Hydrocortisone, Fluocinonide, Isosorbide dinitrate, Zinc chloride, Procaine, Prednisolone, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (57707, 'Enfortumab vedotin', 'Diltiazem', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of unconjugated monomethyl auristatin E (MMAE), the anti-mitotic and cytotoxic component of enfortumab vedotin. Enfortumab vedotin is an antibody-drug conjugate (ADC) that releases MMAE via proteolytic cleavage, and MMAE has been shown in vitro to be primarily metabolized by CYP450 3A4.', 'DDInter', 'Caution is advised when enfortumab vedotin is used concomitantly with CYP450 3A4 inhibitors. Patients should be closely monitored for development or exacerbation of toxicities such as peripheral neuropathy, hyperglycaemia, dermatologic reactions (maculopapular rash, pruritus, symmetrical drug-related intertriginous and flexural exanthema (SDRIFE), bullous dermatitis, exfoliative dermatitis, palmar-plantar erythrodysesthesia), and ocular disorders (dry eyes, keratitis, blurred vision, limbal stem cell deficiency), and the dosing of enfortumab vedotin adjusted or withheld as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when enfortumab vedotin is used concomitantly with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/57783/', '', 'Lidocaine, Zinc sulfate, Tetracaine, Zinc acetate, Fluocinolone acetonide, Hydrocortisone, Fluocinonide, Isosorbide dinitrate, Zinc chloride, Procaine, Prednisolone, More', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (57708, 'Entrectinib', 'Diltiazem', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of entrectinib, which is primarily metabolized by the isoenzyme. Increased exposure to entrectinib may increase the risk and/or severity of adverse effects such as cognitive impairment, mood disorders, dizziness, sleep disturbances, liver enzyme elevations, hyperuricemia, congestive heart failure, edema, myocarditis, QT prolongation, vision problems, anemia, and neutropenia.', 'DDInter', 'Concomitant use of entrectinib with moderate CYP450 3A4 inhibitors should be avoided when possible. If coadministration is necessary, the dosage of entrectinib should be reduced to 100 mg once daily for adults and pediatrics patients (12 years and older) with a body surface area (BSA) greater than 1.5 m2. Following discontinuation, and after an appropriate washout period of the CYP450 3A4 inhibitor (i.e., 3 to 5 half-lives), the entrectinib dosage taken prior to initiating the CYP450 3A4 inhibitor may be resumed. For pediatric patients with a body surface area of 1.5 m2 or less, concomitant use of entrectinib with moderate CYP450 3A4 inhibitors should be avoided.', 'Metabolism', 'Concomitant use of entrectinib with moderate CYP450 3A4 inhibitors should be avoided when possible.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/57784/', '', 'Lidocaine, Zinc sulfate, Tetracaine, Zinc acetate, Fluocinolone acetonide, Hydrocortisone, Fluocinonide, Isosorbide dinitrate, Zinc chloride, Procaine, Prednisolone, More', 'Regorafenib, Lenvatinib, Erdafitinib, Trametinib, Vandetanib, Dacomitinib, Tivozanib', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (57709, 'Enzalutamide', 'Diltiazem', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 and/or 3A4 may increase the plasma concentrations of enzalutamide, which is primarily metabolized by CYP450 2C8 to its pharmacologically active metabolite, N-desmethyl enzalutamide, and to a lesser extent by CYP450 3A4.', 'DDInter', 'Pharmacologic response to enzalutamide should be monitored more closely whenever a CYP450 2C8 and/or 3A4 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary. Patients should be advised to contact their physician if they experience increased side effects such as seizure, asthenia, fatigue, diarrhea, arthralgia, musculoskeletal pain, paresthesia, hot flushes, peripheral edema, headache, dizziness, insomnia, hematuria, anxiety, and hypertension.', 'Metabolism', 'Pharmacologic response to enzalutamide should be monitored more closely whenever a CYP450 2C8 and/or 3A4 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/57785/', '', 'Tetracaine, Zinc sulfate, Zinc acetate, Fluocinolone acetonide, Fluocinonide, Isosorbide dinitrate, Zinc chloride, Procaine, Fluorometholone, Zinc gluconate, Nitroglycerin', 'Degarelix, Abarelix, Nilutamide, Abiraterone', 1767369485);
