INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (57755, 'Fostamatinib', 'Diltiazem', 'Moderate', 'Coadministration of fostamatinib with inhibitors of CYP450 3A4 may increase exposure to the active metabolite known as R406, the predominant moiety in the systemic circulation following fostamatinib administration. Fostamatinib is metabolized in the gut by alkaline phosphatase to R406, which then undergoes oxidation via CYP450 3A4 and glucuronidation via UGT1A9. In vitro, R406 is also a substrate of the P-glycoprotein (P-gp) efflux transporter.', 'DDInter', 'Patients should be monitored for toxicities of fostamatinib during concomitant use of potent or moderate CYP450 3A4 inhibitors or dual CYP450 3A4/P-gp inhibitors, and the fostamatinib dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/57831/', '', 'Tetracaine, Zinc sulfate, Zinc acetate, Fluocinolone acetonide, Fluocinonide, Isosorbide dinitrate, Zinc chloride, Procaine, Fluorometholone, Zinc gluconate, Nitroglycerin', 'Eltrombopag, Romiplostim, Avatrombopag, Emicizumab, Coagulation factor VIIa Recombinant Human, Coagulation factor X human, Anti-inhibitor coagulant complex, Antihemophilic factor, human recombinant, Fibrinogen human, Coagulation Factor IX Human', 1767369485);
