INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (57824, 'Diltiazem', 'Lapatinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of lapatinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Caution is advised when lapatinib is prescribed with CYP450 3A4 inhibitors. Pharmacologic response to lapatinib should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the lapatinib dosage adjusted as necessary. Patients should be advised to contact their physician if they experience increased adverse effects of lapatinib such as diarrhea, nausea, vomiting, dyspepsia, and fatigue.', 'Metabolism', 'Caution is advised when lapatinib is prescribed with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/57900/', '', 'Tetracaine, Zinc sulfate, Zinc acetate, Fluocinolone acetonide, Fluocinonide, Isosorbide dinitrate, Zinc chloride, Procaine, Fluorometholone, Zinc gluconate, Nitroglycerin', 'Regorafenib, Lenvatinib, Erdafitinib, Trametinib, Vandetanib, Dacomitinib, Tivozanib', 1767369485);
