INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (57827, 'Diltiazem', 'Lefamulin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 and/or P-gp may significantly increase the plasma concentrations of lefamulin, which is a substrate of both the isoenzyme and efflux transporter. Coadministration of intravenous lefamulin with oral ketoconazole increased mean lefamulin Cmax and AUC by 6% and 31%, respectively. Increased exposure to lefamulin may increase the risk of QT interval prolongation, which has been associated with ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Concomitant use of lefamulin with potent inhibitors of CYP450 3A4 and/or P-gp should generally be avoided.', 'Metabolism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/57903/', '', 'Tetracaine, Zinc sulfate, Zinc acetate, Fluocinolone acetonide, Fluocinonide, Isosorbide dinitrate, Zinc chloride, Procaine, Fluorometholone, Zinc gluconate, Nitroglycerin', 'Tinidazole, Bacitracin, Polymyxin B, Oritavancin, Daptomycin, Telavancin, Methenamine, Spectinomycin, Fosfomycin, Tedizolid, Dalbavancin', 1767369485);
