INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (57930, 'Diltiazem', 'Pancuronium', 'Moderate', 'Some calcium channel blockers may enhance the neuromuscular blocking properties of nondepolarizing muscle relaxants. The mechanism is not known, but may involve blockage of calcium channels on skeletal muscle. Data are available for verapamil, diltiazem, nicardipine, atracurium, vecuronium, and pancuronium.', 'DDInter', 'Patients should be closely monitored for prolonged neuromuscular blockade. Reduced doses of muscle relaxants may be necessary. Profound neuromuscular blockade may be reversed by neostigmine or edrophonium.', 'Synergism', 'Patients should be closely monitored for prolonged neuromuscular blockade.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/58006/', '', 'Tetracaine, Zinc sulfate, Zinc acetate, Fluocinolone acetonide, Fluocinonide, Zinc chloride, Fluorometholone, Zinc gluconate', 'Botulinum toxin type A', 1767369485);
