INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (58076, 'Diltiazem', 'Treprostinil', 'Moderate', 'Coadministration of medicines targeting the prostacyclin pathway (e.g., prostacyclin (PGI2), PGI2 analogs, or selective non-prostanoid prostacyclin IP receptor agonists) with diuretics, antihypertensive agents, or other vasodilators may potentiate the hypotensive effects of these agents. The mechanism involves the additive reduction in blood pressure due to the vasodilatory effects of PGI2, PGI2 analogs or selective non-prostanoid prostacyclin IP receptor agonists on the prostacyclin pathway.', 'DDInter', 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently. If these drugs are used together, it is generally recommended that blood pressure be measured more frequently until a stable blood pressure pattern is observed. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their doctor if they experience dizziness, lightheadedness, syncope, orthostatic hypotension, or tachycardia.', 'Synergism', 'While therapies that target the prostacyclin pathway have been used in combination with diuretics, antihypertensives, or other vasodilators in the management of pulmonary arterial hypertension, caution is recommended if they must be administered concurrently.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/58152/', '', 'Tetracaine, Zinc sulfate, Zinc acetate, Fluocinolone acetonide, Hydrocortisone, Fluocinonide, Zinc chloride, Procaine, Prednisolone, Betamethasone, Fluorometholone, More', 'Lepirudin, Urokinase, Cangrelor, Caplacizumab, Argatroban, Prasugrel, Desirudin, Streptokinase, Tinzaparin, Vorapaxar, Enoxaparin, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (58077, 'Diltiazem', 'Triazolam', 'Moderate', 'Diltiazem and verapamil both have been reported to increase peak plasma concentration and to prolong the elimination half-life of midazolam. The mechanism probably is related to an increase in the bioavailability and a decrease in the plasma clearance of midazolam. Diltiazem, verapamil, midazolam, and triazolam all appear to be metabolized by the CYP450 (3A4) enzyme.', 'DDInter', 'If diltiazem or verapamil must be used with midazolam or triazolam, close observation for increased and prolonged midazolam or triazolam effect is recommended. Some investigators have suggested that midazolam dosage be reduced by at least 50% in patients who are receiving diltiazem or verapamil. Other investigators have suggested that triazolam be avoided in patients receiving diltiazem.', 'Metabolism', 'If diltiazem or verapamil must be used with midazolam or triazolam, close observation for increased and prolonged midazolam or triazolam effect is recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/58153/', '', 'Zinc sulfate, Zinc acetate, Fluocinolone acetonide, Fluocinonide, Zinc chloride, Fluorometholone, Zinc gluconate', 'Thiopental, Ramelteon, Scopolamine, Methohexital, Ethchlorvynol, Melatonin, Dichloralphenazone, Valerian', 1767369485);
