INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (58093, 'Diltiazem', 'Vemurafenib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may significantly decrease the plasma concentrations of diltiazem, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Use of diltiazem with rifampin or any known CYP450 3A4 inducer should be avoided when possible, and alternative therapy considered. Diltiazem dose adjustment should be guided by clinical response if concomitant use with a CYP450 3A4 inducer is necessary.', 'Metabolism', 'Use of diltiazem with rifampin or any known CYP450 3A4 inducer should be avoided when possible, and alternative therapy considered.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/58169/', '', 'Tetracaine, Zinc sulfate, Zinc acetate, Fluocinolone acetonide, Hydrocortisone, Fluocinonide, Isosorbide dinitrate, Zinc chloride, Procaine, Prednisolone, Betamethasone, More', 'Tinidazole, Ascorbic acid, Hydroflumethiazide, Sulfadiazine, Lactic acid, Methazolamide, Trichlormethiazide, Ciclopirox, Tioconazole, Diiodohydroxyquinoline, Benzthiazide, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (58094, 'Diltiazem', 'Venetoclax', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of venetoclax, which is a substrate of the isoenzyme.', 'DDInter', 'Concomitant use of venetoclax with moderate CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends that venetoclax dosage be reduced by at least 50%. The dosage used prior to initiating the CYP450 3A4 inhibitor may be resumed 2 to 3 days after discontinuation of the inhibitor.', 'Metabolism', 'Concomitant use of venetoclax with moderate CYP450 3A4 inhibitors should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/58170/', '', 'Lidocaine, Zinc sulfate, Tetracaine, Zinc acetate, Fluocinolone acetonide, Hydrocortisone, Fluocinonide, Isosorbide dinitrate, Zinc chloride, Procaine, Prednisolone, More', 'Pertuzumab, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Sotorasib, Enasidenib, Selinexor, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (58095, 'Diltiazem', 'Venlafaxine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of venlafaxine and its metabolite, O-desmethylvenlafaxine (ODV). The possibility of prolonged and/or increased pharmacologic effects of venlafaxine should be considered. Although venlafaxine is primarily metabolized by CYP450 2D6, there is some evidence that CYP450 3A4 may play a minor role, thus any alteration in its activity levels could conceivably affect the metabolism of venlafaxine. The interaction may be of greater concern in patients who are CYP450 2D6-deficient, or so-called poor metabolizers of CYP450 2D6 (approximately 7% of Caucasians and less than 2% of Asians and individuals of African descent).', 'DDInter', 'Caution is recommended when venlafaxine is used with CYP450 3A4 inhibitors.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/58171/', '', 'Oxitriptan, Viloxazine, Vortioxetine, Milnacipran, Tryptophan, Levomilnacipran, Esketamine', 'Tetracaine, Zinc sulfate, Zinc acetate, Fluocinolone acetonide, Fluocinonide, Zinc chloride, Procaine, Betamethasone, Fluorometholone, Zinc gluconate', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (58096, 'Diltiazem', 'Verapamil', 'Moderate', 'Coadministration with diltiazem may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by diltiazem.', 'DDInter', 'Caution is advised if diltiazem must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever diltiazem is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if diltiazem must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/58172/', '', 'Verapamil, Tetracaine, Zinc sulfate, Zinc acetate, Fluocinolone acetonide, Fluocinonide, Zinc chloride, Procaine, Fluorometholone, Zinc gluconate', 'Diltiazem', 1767369485);
