INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (58107, 'Diltiazem', 'Zanubrutinib', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of zanubrutinib, which is primarily metabolized by the isoenzyme. Increased zanubrutinib exposure may potentiate the risk of toxicities such as hemorrhage, infection, cytopenias, malignancies, and atrial fibrillation or flutter.', 'DDInter', 'The manufacturer recommends reducing the dosage of zanubrutinib to 80 mg twice daily when coadministered with moderate CYP450 3A4 inhibitors. Patients should be closely monitored for development of zanubrutinib-related toxicities, and further dosage adjustments made or treatment withheld as needed in accordance with the product labeling. Following discontinuation of the CYP450 3A4 inhibitor, the previous dosage of zanubrutinib should be resumed.', 'Metabolism', 'The manufacturer recommends reducing the dosage of zanubrutinib to 80 mg twice daily when coadministered with moderate CYP450 3A4 inhibitors.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/58183/', '', 'Lidocaine, Zinc sulfate, Tetracaine, Zinc acetate, Fluocinolone acetonide, Hydrocortisone, Fluocinonide, Isosorbide dinitrate, Zinc chloride, Procaine, Prednisolone, More', 'Regorafenib, Lenvatinib, Erdafitinib, Trametinib, Vandetanib, Dacomitinib, Tivozanib', 1767369485);
