INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (58501, 'Cariprazine', 'Lomitapide', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of cariprazine and its major active metabolite, didesmethyl cariprazine (DDCAR), both of which are primarily metabolized by the isoenzyme. The extent to which other, less potent inhibitors of CYP450 3A4 may interact with cariprazine and its metabolites is unknown.', 'DDInter', 'Caution is advised when cariprazine is prescribed with CYP450 3A4 inhibitors. Patients should be monitored for adverse effects such as extrapyramidal symptoms, cognitive and motor impairment, hyperglycemia, dyslipidemia, weight gain, orthostatic hypotension, leucopenia, neutropenia, seizures and dysphagia, and the dosage of cariprazine adjusted as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when cariprazine is prescribed with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/58577/', '', 'Fluphenazine, Haloperidol, Thiothixene, Risperidone, Amisulpride, Prochlorperazine, Promazine, Thioridazine, Trifluoperazine, Paliperidone, Chlorpromazine, More', 'Cholestyramine, Rosuvastatin, Fenofibrate, Mipomersen, Evinacumab, Dextrothyroxine, Simvastatin, Probucol, Cerivastatin, Ezetimibe, Niacin, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (58502, 'Cariprazine', 'Lonafarnib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of cariprazine and its major active metabolite, didesmethyl cariprazine (DDCAR), both of which are primarily metabolized by the isoenzyme.', 'DDInter', 'The dosage of cariprazine should be reduced in the presence of a potent CYP450 3A4 inhibitor. When initiating a potent CYP450 3A4 inhibitor during stable treatment with cariprazine, the manufacturer recommends reducing the current dosage of cariprazine by one-half. For patients taking 4.5 mg daily, the dosage should be reduced to 1.5 mg or 3 mg daily. For patients taking 1.5 mg daily, the dosing interval should be adjusted to every other day. Conversely, if cariprazine therapy is initiated during treatment with a potent CYP450 3A4 inhibitor, the recommended starting dosage of cariprazine is 1.5 mg on day 1 and day 3, with no dose administered on day 2. From day 4 onward, the dosage should be 1.5 mg daily, then increased to a maximum dosage of 3 mg daily. Following withdrawal of the CYP450 3A4 inhibitor, the dosage of cariprazine may need to be increased.', 'Metabolism', 'The dosage of cariprazine should be reduced in the presence of a potent CYP450 3A4 inhibitor.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/58578/', '', 'Chlorpromazine, Mesoridazine, Methotrimeprazine, Prochlorperazine, Fluphenazine, Promazine, Asenapine, Thiothixene, Thioridazine, Perphenazine, Olanzapine, More', 'Teduglutide, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, Agalsidase beta, More', 1767369485);
