INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (58530, 'Cariprazine', 'Metreleptin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 is expected to alter the pharmacokinetics of cariprazine and its major active metabolites. Cariprazine is primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2D6 to desmethyl cariprazine (DCAR) and didesmethyl cariprazine (DDCAR). DCAR is further metabolized to DDCAR by CYP450 3A4 and 2D6, and DDCAR is then metabolized by CYP450 3A4 to a hydroxylated metabolite. Both DCAR and DDCAR have in vitro receptor binding profiles similar to the parent drug and are considered pharmacologically equipotent to cariprazine.', 'DDInter', 'Concomitant use of cariprazine with CYP450 3A4 inducers has not been evaluated and is not recommended.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/58606/', '', 'Fluphenazine, Haloperidol, Thiothixene, Risperidone, Amisulpride, Prochlorperazine, Promazine, Thioridazine, Pimozide, Trifluoperazine, Paliperidone, More', 'Teduglutide, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, Agalsidase beta, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (58531, 'Cariprazine', 'Metyrosine', 'Moderate', 'Coadministration of metyrosine in combination with haloperidol, phenothiazines, or other dopamine antagonists may increase the risk of extrapyramidal disorders. Extrapyramidal effects have been reported during metyrosine therapy. The mechanism is inhibition of catecholamine synthesis by metyrosine. In addition, sedative effects of both drugs may be increased.', 'DDInter', 'When concurrent use is required, patients should be monitored for extrapyramidal symptoms and instructed to notify their physician if they experience symptoms such as bradykinesia, hypertonia, rigidity, restlessness, and dysphagia. Patients should also avoid driving or operating hazardous machinery until they know how the medications affect them.', 'Synergism', 'When concurrent use is required, patients should be monitored for extrapyramidal symptoms and instructed to notify their physician if they experience symptoms such as bradykinesia, hypertonia, rigidity, restlessness, and dysphagia.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/58607/', '', 'Riociguat, Ambrisentan, Macitentan, Tadalafil', 'Quetiapine, Asenapine, Pimavanserin, Ziprasidone, Clozapine, Olanzapine, Pimozide, Risperidone, Loxapine, Paliperidone', 1767369485);
