INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (58992, 'Cimetidine', 'Carmustine', 'Major', 'Coadministration with cimetidine may potentiate the myelosuppressive effects of systemic carmustine. The exact mechanism of interaction is unknown, but may involve additive myelosuppression or inhibition by cimetidine of the hepatic clearance of carmustine.', 'DDInter', 'Caution is advised if systemic carmustine is used in combination with cimetidine. Patients should be closely monitored for the development of delayed myelosuppression that may occur 4 to 6 weeks after carmustine administration. Blood counts are recommended weekly for at least 6 weeks after a dose. Alternatively, other H2-receptor antagonists such as ranitidine and famotidine may be considered in patients who require treatment with carmustine, since they have not been reported to cause the interaction and generally have minimal effects on hepatic metabolism.', 'Synergism', 'Caution is advised if systemic carmustine is used in combination with cimetidine.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59068/', '', 'Temozolomide, Thiotepa, Busulfan, Streptozocin, Chlorambucil, Dacarbazine, Mechlorethamine, Ifosfamide, Melphalan flufenamide, Uracil mustard', 'Tinidazole, Tetracycline, Dexlansoprazole, Rabeprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Rifabutin, Nizatidine, Esomeprazole, Clarithromycin, More', 1767369485);
