INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (59124, 'Berotralstat', 'Spironolactone', 'Major', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may significantly increase the plasma concentrations of berotralstat. Berotralstat is a substrate of both P-gp and BCRP.', 'DDInter', 'The dosage of berotralstat should be reduced to 110 mg once daily in patients requiring chronic coadministration with P-gp and/or BCRP inhibitors. Patients should be advised to contact their physician if they experience undue adverse effects of berotralstat such as abdominal pain, vomiting, or diarrhea. Patients should seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Absorption', 'The dosage of berotralstat should be reduced to 110 mg once daily in patients requiring chronic coadministration with P-gp and/or BCRP inhibitors.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59200/', '', 'Amiloride, Triamterene', 'Hemin, Voxelotor, Conestat alfa, Betibeglogene autotemcel, Hyaluronidase, Streptokinase, Human C1-esterase inhibitor, Crizanlizumab, Lanadelumab, Icatibant', 1767369485);
