INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (59418, 'Fenoldopam', 'Carteolol (ophthalmic)', 'Moderate', 'The use of fenoldopam in combination with beta-blockers has not been studied. Theoretically, beta-blockers may potentiate the risk of severe, symptomatic hypotension by inhibiting the sympathetic reflex response to fenoldopam-induced blood pressure decreases.', 'DDInter', 'Concomitant use of fenoldopam with beta-blockers should be avoided if possible. The same precaution may be applicable to beta-blocker ophthalmic preparations, as they are systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels. Moreover, since fenoldopam may cause a dose-dependent increase in intraocular pressure, caution should be exercised if it is administered to patients with glaucoma or ocular hypertension receiving ophthalmic beta-blockers.', 'Synergism', 'Concomitant use of fenoldopam with beta-blockers should be avoided if possible.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59496/', '', 'Latanoprost, Neostigmine, Pilocarpine, Physostigmine, Acetylcholine, Bimatoprost, Travoprost, Brinzolamide, Epinephrine, Brimonidine, Levobunolol', 'Droxidopa, Amrinone, Angiotensin II, Milrinone, Methoxamine', 1767369485);
