INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (59627, 'Empagliflozin', 'Carvedilol', 'Moderate', 'Sodium-glucose co-transporter 2 (SGLT-2) inhibitors may potentiate the hypotensive effects of diuretics and other antihypertensive agents or vasodilators. Inhibition of glucose and sodium co-transport produces mild diuresis and transient natriuresis, resulting in intravascular volume contraction. Volume depletion-related adverse reactions including hypotension, postural dizziness, orthostatic hypotension, syncope, and dehydration can occur after initiating treatment with SGLT-2 inhibitors, and the risk may be increased with concomitant use of other agents that can lower blood pressure.', 'DDInter', 'Caution is advised if SGLT-2 inhibitors are coadministered with diuretics and other hypotensive agents, particularly in the elderly and patients with impaired renal function. Prior to initiating SGLT-2 inhibitors, volume status should be assessed and corrected, if necessary. Clinical and laboratory monitoring are recommended during therapy, including electrolytes, fluid status, renal function, and blood pressure. If volume depletion occurs, treatment with SGLT-2 inhibitors should be interrupted until the condition is corrected.', 'Synergism', 'Caution is advised if SGLT-2 inhibitors are coadministered with diuretics and other hypotensive agents, particularly in the elderly and patients with impaired renal function.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59708/', '', 'Acetylsalicylic acid, Ivabradine', 'Lixisenatide, Albiglutide, Guar gum, Pramlintide, Sitagliptin, Alogliptin, Liraglutide, Troglitazone, Semaglutide, Linagliptin, Dulaglutide, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (59628, 'Ephedrine', 'Carvedilol', 'Moderate', 'Beta-blockers may antagonize the cardiostimulatory effects of ephedrine by blocking beta-1 adrenergic receptors in the heart. Noncardioselective beta-blockers can antagonize the bronchodilating effects of ephedrine by blocking beta-2 adrenergic receptors in smooth muscles of the bronchial tree. The interaction is less likely to occur with cardioselective beta-blockers, which generally have little effect on beta-2 adrenergic receptors at therapeutic dosages.', 'DDInter', 'Clinicians should be alert to the potential for diminished cardiac response when parenteral ephedrine is used in patients treated with beta-blockers, including ophthalmic formulations. Noncardioselective beta-blockers, including ophthalmic formulations, should generally be avoided in patients using ephedrine-containing preparations for bronchospastic diseases. If beta-blocker therapy is necessary, an agent with beta-1 selectivity (e.g., atenolol, metoprolol, betaxolol) is considered safer. However, caution is advised, especially with higher dosages of the beta-blocker.', 'Antagonism', 'Clinicians should be alert to the potential for diminished cardiac response when parenteral ephedrine is used in patients treated with beta-blockers, including ophthalmic formulations.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59709/', '', 'Felodipine, Amlodipine, Nifedipine, Ivabradine, Acetylsalicylic acid', 'Tropicamide, Ciclesonide, Mometasone furoate, Mometasone, Vitamin A, Olopatadine, Cromoglicic acid, Ipratropium, Hyaluronic acid, Nedocromil, Beclomethasone dipropionate, More', 1767369485);
