INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (59905, 'Carvedilol', 'Tizanidine', 'Major', 'Tizanidine may potentiate the hypotensive effect of some medications secondary to its alpha-2 adrenergic activity. Pharmacologic studies have found tizanidine to possess between 1/10 to 1/50 of the potency of clonidine, a structurally similar agent, in lowering blood pressure.', 'DDInter', 'A lower initial dosage and cautious dosage titration should be considered when tizanidine is initiated in patients receiving hypotensive medications. Although single doses of less than 8 mg of tizanidine have not been shown effective for spasticity in controlled clinical studies, it may be prudent to initiate treatment with 4 mg doses and gradually increase in 2 to 4 mg increments until optimum effect is achieved. The dose can be repeated at 6 to 8 hour intervals as needed, up to a maximum of three doses in 24 hours and a total daily dosage of 36 mg. However, experience with single doses exceeding 8 mg and daily doses exceeding 24 mg is limited. Close monitoring for development of hypotension is recommended.', 'Synergism', 'A lower initial dosage and cautious dosage titration should be considered when tizanidine is initiated in patients receiving hypotensive medications.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59987/', '', '', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (59906, 'Carvedilol', 'Tocainide', 'Moderate', 'The concomitant use of tocainide and beta-blockers may have additive effects on cardiac index, left ventricular function, and pulmonary wedge pressure. While significant interactions have not been reported in studies, cases of asystole have been reported in patients with preexisting electrophysiologic abnormalities after tocainide and metoprolol were coadministered. The mechanism is not known, but may be related to an additive effect on the sinus node or conduction system, especially in patients with sick sinus syndrome. Cases of confusion and paranoia have also been reported after coadministration of tocainide and propranolol.', 'DDInter', 'The concomitant use of tocainide and beta blockers should probably be avoided in patients with atrioventricular conduction abnormalities or sick sinus syndrome. This combination should be used cautiously in other patients, with careful dose titration and close clinical and electrocardiographic monitoring for efficacy and safety.', 'Synergism', 'The concomitant use of tocainide and beta blockers should probably be avoided in patients with atrioventricular conduction abnormalities or sick sinus syndrome.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/59988/', '', 'Bretylium, Moricizine, Procainamide, Ibutilide', 'Nebivolol, Felodipine, Amlodipine, Nebivolol, Nifedipine, Ivabradine, Acetylsalicylic acid', 1767369485);
