INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (59931, 'Carvedilol', 'Vemurafenib', 'Moderate', 'Coadministration with vemurafenib may increase the plasma concentrations of drugs that are substrates of the CYP450 1A2 and/or 2D6 isoenzymes. The mechanism is decreased clearance due to inhibition of CYP450 1A2 and 2D6 activity by vemurafenib. The interaction has been studied with caffeine and dextromethorphan, probe substrates for CYP450 1A2 and 2D6, respectively.', 'DDInter', 'Caution is advised if vemurafenib must be used concurrently with drugs that undergo metabolism by CYP450 1A2 and/or 2D6. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever vemurafenib is added to or withdrawn from therapy. Concomitant use of vemurafenib and substrates of CYP450 1A2 and/or 2D6 with a narrow therapeutic index (e.g., antiarrhythmics; theophylline) should be avoided if possible.', 'Metabolism', 'Caution is advised if vemurafenib must be used concurrently with drugs that undergo metabolism by CYP450 1A2 and/or 2D6.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60013/', '', 'Acebutolol, Labetalol, Nadolol, Esmolol, Betaxolol, Penbutolol, Bisoprolol, Carteolol, Atenolol, Felodipine, Amlodipine, More', 'Tinidazole, Ascorbic acid, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Ciclopirox, Darunavir, Tioconazole, Diiodohydroxyquinoline, More', 1767369485);
