INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (60198, 'Cefoxitin', 'Torasemide', 'Moderate', 'Limited data suggest that furosemide and possibly other loop diuretics may potentiate the nephrotoxicity of some cephalosporins. The exact mechanism of interaction is unknown, although furosemide has been shown to increase the plasma concentrations and/or reduce the clearance of several cephalosporins such as cephaloridine and ceftazidime.', 'DDInter', 'Although data are primarily limited to cephaloridine, which is no longer commercially marketed, caution may be advisable in patients receiving a loop diuretic in combination with other cephalosporins. Renal function should be monitored, particularly when high dosages are used or when these medications are administered in the elderly or patients with preexisting renal impairment.', 'Synergism', 'Although data are primarily limited to cephaloridine, which is no longer commercially marketed, caution may be advisable in patients receiving a loop diuretic in combination with other cephalosporins.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60280/', '', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, More', 'Imipenem, Aztreonam, Meropenem, Cefiderocol, Ertapenem, Doripenem', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (60199, 'Cefoxitin', 'Typhoid vaccine (live)', 'Major', 'INTERVAL: The use of live, attenuated oral typhoid vaccine with antibacterial sulfonamides or other antibiotics may result in a diminished immunologic response to the vaccine. Some antibiotics may be active against the vaccine strain of Salmonella typhi (Ty21a), thereby preventing a sufficient degree of multiplication to occur in order to induce a protective immune response.', 'DDInter', 'Live, attenuated oral typhoid vaccine should not be administered during and for at least 3 days before and after treatment with antibacterial sulfonamides or other antibiotics. A longer interval should be considered following treatment with long-acting antibiotics (e.g., azithromycin). If malaria prophylaxis is needed, the same 3-day interval at the minimum should be observed between antimalarials and the vaccine. However, chloroquine, mefloquine, and pyrimethamine-sulfadoxine may be given concomitantly with the vaccine. Alternatively, parenteral typhoid vaccine (typhoid Vi polysaccharide vaccine) may be considered if coadministration with antibacterial agents is required.', 'Others', 'Live, attenuated oral typhoid vaccine should not be administered during and for at least 3 days before and after treatment with antibacterial sulfonamides or other antibiotics.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60281/', '', '', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (60200, 'Cefoxitin', 'Warfarin', 'Moderate', 'Cephalosporins may potentiate the anticoagulant effects of vitamin K antagonists. Possible mechanisms include inhibited growth of vitamin K-producing intestinal bacteria, inhibited production of vitamin K-dependent clotting factors via a methylthiotetrazole (MTT) side chain (e.g., cefamandole, cefmetazole, cefoperazone, cefotetan), and/or inhibited platelet activity.', 'DDInter', 'Prothrombin time and INR should be monitored and the patient closely observed for signs of bleeding. The anticoagulant dose may be adjusted as indicated. Patients should be advised to notify their physicians if they experience any signs or symptoms that may indicate excessive anticoagulation, such as unusual or prolonged bleeding, bruising, coffee ground emesis, change in stool or urine color, headache, dizziness, or weakness.', 'Synergism', 'Prothrombin time and INR should be monitored and the patient closely observed for signs of bleeding.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60282/', '', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, More', 'Meropenem, Doripenem', 1767369485);
