INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (60395, 'Ceftriaxone', 'Verapamil', 'Moderate', 'According to a single case report, when ceftriaxone and clindamycin were added to the regimen of a patient who was receiving a stable dosage of verapamil and other medications, acute verapamil toxicity resulted. The patient developed complete heart block. The mechanism is unknown and causality was not clearly determined. The purported mechanism (displacement of verapamil from protein binding sites) has been disputed.', 'DDInter', 'Until more information is available, patients should be monitored for verapamil toxicity.', 'Distribution', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60481/', '', 'Trandolapril, Moexipril, Benazepril', 'Cefepime, Imipenem, Aztreonam, Cefiderocol, Cefapirin, Cefoxitin, Cefprozil, Cefonicid, Cefoperazone, Cefditoren, Meropenem, More', 1767369485);
