INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (60398, 'Amikacin (liposome)', 'Cefuroxime', 'Moderate', 'Coadministration of aminoglycosides and cephalosporins may increase the risk of nephrotoxicity. An increased incidence of nephrotoxicity has been reported during concomitant use of aminoglycosides and some, mostly older cephalosporins (e.g., cefaloridine, cefamandole, cefazolin, cefotaxime, cefoxitin, ceftazidime, cefuroxime, cephalothin, ceftriaxone). The risk may be greatest in the elderly or patients with preexisting renal impairment, when large doses are used, and during prolonged treatment.', 'DDInter', 'The lowest effective dosages of aminoglycosides and cephalosporins should be used when they are prescribed in combination. Renal function should be monitored closely. The same precaution may be applicable when aminoglycosides are administered via irrigation, intrapleurally, intraperitoneally or orally, since aminoglycosides can be systemically absorbed via these routes; however, clinical data are lacking.', 'Synergism', 'The lowest effective dosages of aminoglycosides and cephalosporins should be used when they are prescribed in combination.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60484/', '', 'Rifamycin, Oxytetracycline, Lomefloxacin, Sulfamethizole, Trifluridine, Cefuroxime, Benzylpenicillin, Ampicillin, Sulfisoxazole, Chlorhexidine, Levofloxacin, More', 'Gatifloxacin, Rifamycin, Lomefloxacin, Ampicillin, Azithromycin, Ofloxacin, Acyclovir, Ciprofloxacin, Famciclovir, Trifluridine, Moxifloxacin, More', 1767369485);
