INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (60438, 'Cefuroxime', 'Typhoid vaccine (live)', 'Major', 'INTERVAL: The use of live, attenuated oral typhoid vaccine with antibacterial sulfonamides or other antibiotics may result in a diminished immunologic response to the vaccine. Some antibiotics may be active against the vaccine strain of Salmonella typhi (Ty21a), thereby preventing a sufficient degree of multiplication to occur in order to induce a protective immune response.', 'DDInter', 'Live, attenuated oral typhoid vaccine should not be administered during and for at least 3 days before and after treatment with antibacterial sulfonamides or other antibiotics. A longer interval should be considered following treatment with long-acting antibiotics (e.g., azithromycin). If malaria prophylaxis is needed, the same 3-day interval at the minimum should be observed between antimalarials and the vaccine. However, chloroquine, mefloquine, and pyrimethamine-sulfadoxine may be given concomitantly with the vaccine. Alternatively, parenteral typhoid vaccine (typhoid Vi polysaccharide vaccine) may be considered if coadministration with antibacterial agents is required.', 'Others', 'Live, attenuated oral typhoid vaccine should not be administered during and for at least 3 days before and after treatment with antibacterial sulfonamides or other antibiotics.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60524/', '', 'Ganciclovir, Acyclovir, Famciclovir, Chlorhexidine, Povidone-iodine, Fluconazole', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (60439, 'Cefuroxime', 'Warfarin', 'Moderate', 'Cephalosporins may potentiate the anticoagulant effects of vitamin K antagonists. Possible mechanisms include inhibited growth of vitamin K-producing intestinal bacteria, inhibited production of vitamin K-dependent clotting factors via a methylthiotetrazole (MTT) side chain (e.g., cefamandole, cefmetazole, cefoperazone, cefotetan), and/or inhibited platelet activity.', 'DDInter', 'Prothrombin time and INR should be monitored and the patient closely observed for signs of bleeding. The anticoagulant dose may be adjusted as indicated. Patients should be advised to notify their physicians if they experience any signs or symptoms that may indicate excessive anticoagulation, such as unusual or prolonged bleeding, bruising, coffee ground emesis, change in stool or urine color, headache, dizziness, or weakness.', 'Synergism', 'Prothrombin time and INR should be monitored and the patient closely observed for signs of bleeding.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/60525/', '', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Caplacizumab, More', 'Ganciclovir, Rifamycin, Kanamycin, Netilmicin, Polymyxin B, Trifluridine, Amikacin, Povidone-iodine, Meropenem, Doripenem, Amikacin, Secnidazole', 1767369485);
