INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (60925, 'Cenobamate', 'Zonisamide', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations and pharmacologic effects of zonisamide, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Pharmacologic response to zonisamide should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the zonisamide dosage adjusted as necessary. Patients should be advised to notify their physician if they experience loss of seizure control.', 'Metabolism', 'Pharmacologic response to zonisamide should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the zonisamide dosage adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/61011/', '', 'Lacosamide, Cenobamate, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Methazolamide, More', 'Ethotoin, Rufinamide, Methsuximide, Zonisamide, Phensuximide, Phenacemide, Trimethadione, Lacosamide, Mephenytoin', 1767369485);
