INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (61968, 'Chenodeoxycholic acid', 'Warfarin', 'Moderate', 'Due to its hepatotoxicity, chenodeoxycholic acid can affect the pharmacodynamics of coumarin derivatives, causing unexpected prolongation of the prothrombin time and increasing the risk of hemorrhages.', 'DDInter', 'Patients on concomitant therapy with chenodeoxycholic acid and coumarin derivatives should be closely monitored. If prolongation of prothrombin time is observed, the coumarin dosage should be adjusted to give a prothrombin time 1.5 to 2 times normal. Discontinuation of chenodeoxycholic acid may be required in some cases. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Antagonism', 'Patients on concomitant therapy with chenodeoxycholic acid and coumarin derivatives should be closely monitored.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62054/', '', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, More', 'Cholic Acid, Odevixibat, Maralixibat', 1767369485);
