INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (62120, 'Chloramphenicol', 'Carbenicillin', 'Moderate', 'Chloramphenicol may inhibit the bactericidal effect of penicillins when used to treat bacterial meningitis and staphylococcal endocarditis. Chloramphenicol acts by inhibiting cellular protein synthesis and can change actively growing bacteria into static ones. Since penicillins work by inhibiting cell wall synthesis, their effectiveness may be reduced. In addition, neurologic toxicity may be enhanced.', 'DDInter', 'Until more information is available, this combination should probably be reserved for conditions where it has been found to be efficacious and avoided in situations where bactericidal activity is necessary, such as certain types of bacterial meningitis or staphylococcal endocarditis. If the combination is necessary, some experts recommend administering the penicillin several hours before the chloramphenicol. Patients should be closely monitored for altered antimicrobial efficacy.', 'Antagonism', 'Until more information is available, this combination should probably be reserved for conditions where it has been found to be efficacious and avoided in situations where bactericidal activity is necessary, such as certain types of bacterial meningitis or staphylococcal endocarditis.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62206/', '', 'Mupirocin, Rifamycin, Bacitracin, Rifaximin, Gentamicin, Amikacin, Neomycin, Rifamycin, Miconazole, Polymyxin B, Ciprofloxacin, More', 'Sulbactam, Dicloxacillin, Bacampicillin, Phenoxymethylpenicillin, Tazobactam', 1767369485);
