INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (62338, 'Chloramphenicol', 'Phenytoin', 'Moderate', 'Chloramphenicol often markedly increases serum phenytoin levels. Toxicity has been reported. Also, phenytoin has been reported both to increase and to decrease serum chloramphenicol levels. Significant systemic absorption of otic or ophthalmic chloramphenicol products is unlikely. However, the clinician should be aware of the possibility of a drug interaction. Similar effects may occur with other hydantoins.', 'DDInter', 'This combination usually should be avoided if possible. Patients should be closely observed for signs of hydantoin toxicity if these drugs must be given concurrently and serum phenytoin concentrations should be monitored. Patients should be advised to notify their physician if they experience symptoms of toxicity, including drowsiness, visual disturbances, change in mental status, seizures, nausea, or ataxia. The hydantoin dose may need to be decreased.', 'Others', 'This combination usually should be avoided if possible.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62424/', '', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Lamotrigine, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Oxcarbazepine, Rufinamide, More', 'Tetracycline, Mupirocin, Rifamycin, Oxytetracycline, Bacitracin, Demeclocycline, Rifaximin, Neomycin, Tetracycline, Rifamycin, Polymyxin B, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (62339, 'Chloramphenicol', 'Pimavanserin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of pimavanserin, which is primarily metabolized by the isoenzyme. High plasma levels of pimavanserin may increase the risk of QT interval prolongation, which has been associated with ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Caution is advised when pimavanserin is used with CYP450 3A4 inhibitors. Patients should be monitored for adverse effects such as peripheral edema and QT prolongation, and the pimavanserin dosage adjusted as necessary.', 'Metabolism', 'Caution is advised when pimavanserin is used with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62425/', '', 'Tetracycline, Mupirocin, Rifamycin, Oxytetracycline, Bacitracin, Demeclocycline, Rifaximin, Gentamicin, Amikacin, Neomycin, Tetracycline, More', 'Fluphenazine, Haloperidol, Thiothixene, Risperidone, Amisulpride, Prochlorperazine, Thioridazine, Trifluoperazine, Paliperidone, Chlorpromazine, Mesoridazine, More', 1767369485);
