INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (62491, 'Disulfiram', 'Chlordiazepoxide', 'Moderate', 'The combination of disulfiram and benzodiazepines may increase CNS depression. The mechanism may relate to the inhibition of hepatic oxidation of benzodiazepines by disulfiram. Diazepam and chlordiazepoxide may interact with disulfiram to a greater extent than other benzodiazepines.', 'DDInter', 'The benzodiazepine dosage may require reduction. Patients should be monitored for excessive and prolonged sedation.', 'Synergism', 'The benzodiazepine dosage may require reduction.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62577/', '', 'Meprobamate, Buspirone, Hydroxyzine', 'Lindane, Acamprosate, Naltrexone, Varenicline, Nalmefene, Nicotine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (62492, 'Chlordiazepoxide', 'Valproic acid', 'Moderate', 'One case series has suggested that benzodiazepines may amplify the teratogenic effects of valproate in the offspring of epileptic women. Both drugs individually have been associated with adverse effects to the fetus. Another study has suggested that valproate may displace diazepam from plasma protein binding sites and inhibit its metabolism; however, the clinical significance has not been established. Other benzodiazepines may interact with valproate in a similar fashion.', 'DDInter', 'Both valproate and benzodiazepines should be avoided during pregnancy unless the potential benefits outweigh the risks to the fetus. In other patients, close observation for clinical evidence of benzodiazepine toxicity (excessive sedation) is recommended if valproate and a benzodiazepine must be used together.', 'Synergism', 'Both valproate and benzodiazepines should be avoided during pregnancy unless the potential benefits outweigh the risks to the fetus.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62578/', '', 'Lacosamide', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (62493, 'Doxacurium', 'Chlordiazepoxide', 'Moderate', 'Benzodiazepines may have variable effects on nondepolarizing muscle relaxants. Potentiation, attenuation, and no effect have all been reported.', 'DDInter', 'Patients should be monitored closely for respiratory difficulty when these agents are used together.', 'Others', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62579/', '', 'Meprobamate, Buspirone, Hydroxyzine', 'Botulinum toxin type A', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (62494, 'Doxazosin', 'Chlordiazepoxide', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/62580/', '', '', '', 1767369485);
