INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (64069, 'Chlorthalidone', 'Ozanimod', 'Moderate', 'Monoamine oxidase inhibitors (MAOIs) may potentiate the hypotensive effect of some medications. MAOIs alone quite commonly produce orthostatic hypotension. This effect may stem from a gradual MAOI-induced accumulation of false neurotransmitters in peripheral adrenergic neurons that have minimal activity at alpha- and beta-adrenergic receptors, resulting in a functional block of sympathetic neurotransmission. The interaction has been reported with the concomitant use of beta-blockers.', 'DDInter', 'Caution is advised during coadministration of MAOIs and other medications with hypotensive effects, especially during the first few weeks of treatment. Close monitoring for development of hypotension is recommended. Ambulatory patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution is advised during coadministration of MAOIs and other medications with hypotensive effects, especially during the first few weeks of treatment.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/64155/', '', 'Tinidazole, Ascorbic acid, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Methazolamide, Amphotericin B, Clindamycin, Ciclopirox, Darunavir, More', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Abatacept, Upadacitinib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (64070, 'Chlorthalidone', 'Paliperidone', 'Moderate', 'Some neuroleptic agents may cause prolongation of the QT interval. While clinical data are lacking, the coadministration of other agents that can produce hypokalemia and/or hypomagnesemia (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins, stimulant laxatives) may result in elevated risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes. In addition, neuroleptic agents may potentiate the hypotensive effect of diuretics secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during the initial dose titration period of neuroleptic therapy.', 'DDInter', 'Caution is advised when neuroleptics must be used concomitantly with medications that can cause potassium and/or magnesium disturbances. Serum electrolytes should be monitored and any abnormalities corrected prior to initiating therapy with a neuroleptic. Close clinical monitoring for development of hypotension is recommended if neuroleptic agents are prescribed with a diuretic medication. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. A lower starting dosage and slower titration of the neuroleptic agent may be appropriate in patients receiving antihypertensive therapy, especially if they are elderly.', 'Synergism', 'Caution is advised when neuroleptics must be used concomitantly with medications that can cause potassium and/or magnesium disturbances.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/64156/', '', 'Tamsulosin, Tinidazole, Ascorbic acid, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Clindamycin, Ciclopirox, More', 'Pimavanserin', 1767369485);
