INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (64697, 'Cholestyramine', 'Levonorgestrel', 'Moderate', 'Coadministration with bile-acid binding resins such as cholestyramine may decrease the plasma concentrations and therapeutic effects of estrogens and progestins. The proposed mechanism involves delaying or reducing the absorption of concomitant orally administered drugs by cholestyramine. In addition, it has also been suggested that bile-acid binding resins may interfere with the enterohepatic recycling of estrogens and therefore increase their elimination.', 'DDInter', 'Estrogens and/or progestin-containing oral medications should be administered at least one hour before or four to six hours after bile-acid binding resins such as cholestyramine, colestipol, and colesevelam. Patients receiving this combination should be advised to closely follow dosing schedules to prevent loss of therapeutic effects.', 'Absorption', 'Estrogens and/or progestin-containing oral medications should be administered at least one hour before or four to six hours after bile-acid binding resins such as cholestyramine, colestipol, and colesevelam.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/64783/', '', 'Ulipristal', 'Gemfibrozil, Fenofibric acid, Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Lovastatin, Clofibrate, Cerivastatin, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (64698, 'Cholestyramine', 'Levothyroxine', 'Moderate', 'INTERVAL: Bile acid sequestrants may bind to and decrease the gastrointestinal absorption of thyroid hormones. There have been isolated reports of worsening hypothyroidism and increased TSH levels following the addition of cholestyramine in patients receiving thyroid replacement therapy.', 'DDInter', 'Thyroid hormone preparations and bile acid sequestrants should be administered at least 4 hours apart. Close clinical and laboratory monitoring of thyroid function is recommended during coadministration, since adjusting the dosing interval may not completely prevent the interaction.', 'Absorption', 'Thyroid hormone preparations and bile acid sequestrants should be administered at least 4 hours apart.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/64784/', '', 'Fenofibric acid, Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Clofibrate, Lomitapide, Bempedoic acid, Alirocumab', '', 1767369485);
