INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (64723, 'Cholestyramine', 'Propranolol', 'Moderate', 'Coadministration with colestipol or cholestyramine may result in an up to 50% decrease in propranolol plasma concentrations. The proposed mechanism is binding of propranolol by these resins. However, clinical data evaluating this interaction are limited.', 'DDInter', 'Until more information is available, caution is advised if colestipol or cholestyramine are used concomitantly with propranolol. Propranolol should be administered at least 1 to 2 hours before or 4 to 6 hours after cholestyramine or colestipol administration. Dosage adjustments as well as clinical monitoring of propranolol should be considered whenever colestipol or cholestyramine is added to or withdrawn from therapy.', 'Others', 'Until more information is available, caution is advised if colestipol or cholestyramine are used concomitantly with propranolol.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/64809/', '', 'Felodipine, Amlodipine, Metoprolol, Carvedilol, Nebivolol, Nifedipine, Bisoprolol, Ivabradine, Sotalol, Atenolol, Acebutolol, More', 'Fenofibric acid, Mipomersen, Pitavastatin, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Clofibrate, Cerivastatin, Lomitapide, Bempedoic acid, More', 1767369485);
