INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (64758, 'Amikacin (liposome)', 'Choline salicylate', 'Moderate', 'The nephrotoxic effect of aminoglycosides may be potentiated by nonsteroidal anti-inflammatory drugs (NSAIDs), particularly if the latter had been given in high dosages for prolonged periods. Animal models suggest that renal prostaglandins may play a role in maintaining normal renal blood flow and glomerular filtration rate during the development of aminoglycoside nephrotoxicity, thus inhibition of their production by NSAIDs may worsen the renal damage. In premature infants, NSAIDs may increase the plasma concentrations of aminoglycosides. The proposed mechanism is decreased aminoglycoside clearance due to NSAID reduction of glomerular filtration rate.', 'DDInter', 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy. If concomitant administration is necessary, hydration status as well as renal and vestibular functions should be closely monitored.', 'Synergism', 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/64844/', '', 'Rifamycin, Ampicillin, Azithromycin, Acyclovir, Polymyxin B, Famciclovir, Cefuroxime, Trifluridine, Benzylpenicillin, Erythromycin, Neomycin, More', 'Acetaminophen, Ziconotide', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (64759, 'Anisindione', 'Choline salicylate', 'Moderate', 'Theoretically, salicylates may potentiate the effects of anticoagulants and increase the risk of bleeding. Salicylates interfere with the action of vitamin K and induce a dose-dependent alteration in hepatic synthesis of coagulation factors VII, IX and X, occasionally increasing the prothrombin time. While these effects are generally slight for most salicylates (except aspirin) at recommended dosages, they may be of clinical significance when combined with the inhibitory effects of anticoagulants on the clotting cascade. Moreover, salicylates are known to cause dose-related gastrointestinal bleeding, which may be complicated by anticoagulant therapy.', 'DDInter', 'Until further data are available, products containing salicylates, especially if given chronically or in high dosages, should preferably be avoided in patients receiving anticoagulants. Close clinical and laboratory observation for bleeding complications is recommended if concurrent therapy is necessary. The same precaution should be observed with the use of salicylate-related agents such as salicylamide because of their structural and pharmacological similarities.', 'Synergism', 'Until further data are available, products containing salicylates, especially if given chronically or in high dosages, should preferably be avoided in patients receiving anticoagulants.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/64845/', '', 'Ziconotide, Methoxyflurane', '', 1767369485);
