INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65423, 'Amprenavir', 'Cimetidine', 'Minor', 'The coadministration with drugs that are inhibitors of the CYP450 3A4 enzymatic pathway may increase the plasma concentrations of amprenavir, which is metabolized by CYP450 3A4. The possibility of prolonged and/or increased pharmacologic effects of amprenavir should be considered. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. Patients should be advised to notify their physician if they experience nausea, vomiting, diarrhea, or a rash.', 'DDInter', '-', 'Metabolism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65510/', '', 'Pantoprazole, Levofloxacin, Tetracycline, Rabeprazole, Dexlansoprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Famotidine, Nizatidine, Esomeprazole, More', 'Paritaprevir, Glecaprevir, Cabotegravir, Simeprevir, Zanamivir, Lenacapavir, Telaprevir, Oseltamivir, Cobicistat, Brincidofovir, Stavudine, More', 1767369485);
