INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65425, 'Cimetidine', 'Astemizole', 'Major', 'Cimetidine inhibits the CYP450 isoenzymes and may interfere with metabolism of the antihistamines terfenadine and astemizole. The accumulation of either of these drugs may result in a prolongation of the QT interval and could lead to the development of ventricular arrhythmias such as torsades de pointes. One case of drug-induced torsades de pointes has been reported in a patient receiving cimetidine and terfenadine. The patient was also receiving other medications including chlorpheniramine, propoxyphene and acetaminophen.', 'DDInter', 'Until more information is available, it may be best to avoid this combination. Cetirizine, fexofenadine, or loratadine may be safer antihistamine alternatives.', 'Metabolism', 'Until more information is available, it may be best to avoid this combination.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65512/', '', 'Tinidazole, Pantoprazole, Tetracycline, Dexlansoprazole, Rabeprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Rifabutin, Nizatidine, Esomeprazole, More', 'Ketotifen, Mepyramine, Levocetirizine, Chlorcyclizine, Acrivastine, Triprolidine, Tripelennamine, Azelastine, Dexbrompheniramine, Clemastine, Doxylamine, More', 1767369485);
