INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65435, 'Cimetidine', 'Cisapride', 'Major', 'Coadministration with cimetidine may increase the plasma concentrations of cisapride. The mechanism is cimetidine inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of cisapride. Conversely, cisapride may accelerate but decrease the extent of gastrointestinal absorption of cimetidine.', 'DDInter', 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of cisapride, concurrent use of cimetidine should be avoided if possible. Other H2-receptor antagonists (e.g., famotidine, nizatidine, ranitidine) have not been shown to significantly inhibit CYP450 3A4 and may be safer alternatives during therapy with cisapride.', 'Metabolism, Absorption', 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of cisapride, concurrent use of cimetidine should be avoided if possible.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65522/', '', 'Tinidazole, Pantoprazole, Tetracycline, Dexlansoprazole, Rabeprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Rifabutin, Nizatidine, Esomeprazole, More', '', 1767369485);
