INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65476, 'Dronedarone', 'Cimetidine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of dronedarone, which is primarily metabolized by the isoenzyme. Data are not available for dronedarone in combination with other, less potent CYP450 3A4 inhibitors.', 'DDInter', 'Caution is advised if dronedarone is prescribed in combination with CYP450 3A4 inhibitors. Pharmacologic response to dronedarone should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. Patients should be advised to seek medical attention if they experience symptoms that could indicate dronedarone toxicity such as excessive fatigue, fainting, difficulty breathing, chest pain or tightness, bradycardia, or symptoms of torsade de pointes such as dizziness, palpitations, and syncope. Dronedarone should be discontinued if QTc Bazett interval increases to 500 ms or greater.', 'Metabolism', 'Caution is advised if dronedarone is prescribed in combination with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65563/', '', 'Pantoprazole, Tetracycline, Rabeprazole, Dexlansoprazole, Sucralfate, Misoprostol, Bismuth subcitrate potassium, Nizatidine, Esomeprazole, Ranitidine, Vonoprazan, More', 'Bretylium, Mexiletine, Ibutilide', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65477, 'Duloxetine', 'Cimetidine', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 may increase the plasma concentrations of duloxetine, which is a substrate of the isoenzyme. Theoretically, high plasma levels of duloxetine may increase the risk of serious adverse effects such as hypertension, hypertensive crisis, increased heart rate, orthostatic hypotension, syncope, and serotonin syndrome. Serotonin syndrome is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Symptoms may include mental status changes such as irritability, altered consciousness, confusion, hallucinations, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea.', 'DDInter', 'Caution is advised if duloxetine is used in combination with CYP450 1A2 inhibitors. Pharmacologic response to duloxetine should be monitored more closely whenever a CYP450 1A2 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary.', 'Metabolism', 'Caution is advised if duloxetine is used in combination with CYP450 1A2 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65564/', '', 'Phenelzine, Tranylcypromine, Maprotiline, St. John''s Wort, Nefazodone, Isocarboxazid, Oxitriptan, Viloxazine, Milnacipran, Vortioxetine, Trazodone, More', 'Tinidazole, Bismuth subcitrate potassium, Rifabutin, Vonoprazan, Levofloxacin', 1767369485);
