INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65490, 'Erlotinib', 'Cimetidine', 'Moderate', 'INTERVAL: Concurrent administration of agents that increase gastric pH such as H2-receptor antagonists may decrease the oral bioavailability of erlotinib and reduce its concentrations in plasma. The solubility of erlotinib decreases when the pH is over 5, resulting in reduced absorption.', 'DDInter', 'In general, the concomitant use of erlotinib with agents that increase gastric pH should be avoided whenever possible. If acid-suppression therapy is required during erlotinib treatment, an H2-receptor antagonist such as ranitidine may be considered. Available pharmacokinetic data suggest that erlotinib should be administered once a day, at least 2 hours before or 10 hours after dosing of the H2-receptor antagonist. Additionally, the lowest effective dosage of the H2-receptor antagonist should be prescribed. It may be appropriate to avoid using cimetidine for long-term acid suppression, as it may inhibit the CYP450 3A4-mediated metabolism of erlotinib and increase the risk of toxicity.', 'Metabolism, Absorption', 'In general, the concomitant use of erlotinib with agents that increase gastric pH should be avoided whenever possible.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65577/', '', 'Tinidazole, Levofloxacin, Tetracycline, Misoprostol, Bismuth subcitrate potassium, Amoxicillin', 'Vemurafenib, Vandetanib, Capmatinib, Afatinib, Tivozanib, Encorafenib, Sunitinib, Regorafenib, Alpelisib, Lenvatinib, Erdafitinib, More', 1767369485);
