INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65529, 'Imipramine', 'Cimetidine', 'Moderate', 'Cimetidine may increase the serum concentration of tricyclic antidepressants (TCAs) to toxic levels in some patients. The mechanism may be related to inhibition of CYP450 isoenzymes, including CYP450 2D6 and CYP450 3A4, that are responsible for the hepatic metabolism of TCAs.', 'DDInter', 'Close monitoring for clinical response and tolerance is recommended whenever cimetidine is added to or discontinued from a TCA regimen. Patients should be advised to notify their physician if they experience excessive TCA effects such as dry mouth, blurry vision, irregular or fast heartbeat, constipation, urinary retention, dizziness, or orthostatic hypotension. Lower doses of tricyclic antidepressants may be necessary. Ranitidine, famotidine, and nizatidine are not expected to interact and may be an alternative to cimetidine in this circumstance.', 'Metabolism', 'Close monitoring for clinical response and tolerance is recommended whenever cimetidine is added to or discontinued from a TCA regimen.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65616/', '', 'Phenelzine, Tranylcypromine, Maprotiline, St. John''s Wort, Nefazodone, Isocarboxazid, Oxitriptan, Viloxazine, Milnacipran, Vortioxetine, Trazodone, More', 'Tinidazole, Tetracycline, Dexlansoprazole, Misoprostol, Bismuth subcitrate potassium, Rifabutin, Nizatidine, Vonoprazan, Amoxicillin', 1767369485);
