INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65541, 'Cimetidine', 'Ketoconazole', 'Moderate', 'H2 antagonists may reduce the bioavailability of ketoconazole by 75% to 80% resulting in decreased plasma ketoconazole concentrations and possible therapeutic failure. The mechanism is related to an increase in gastric pH and a decrease in absorption of the antifungal agent.', 'DDInter', 'Some manufacturers of ketoconazole suggest administering ketoconazole with an acidic beverage (e.g., non-diet cola, orange juice) if used concomitantly with H2 antagonists (US, GB, CA, BR). Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever H2 antagonists are added to or withdrawn from therapy with ketoconazole.', 'Absorption', 'Some manufacturers of ketoconazole suggest administering ketoconazole with an acidic beverage (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65628/', '', 'Tinidazole, Misoprostol, Bismuth subcitrate potassium, Amoxicillin', 'Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, Clindamycin, Trichlormethiazide, More', 1767369485);
