INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (65573, 'Cimetidine', 'Meperidine', 'Moderate', 'Coadministration with cimetidine may increase the plasma concentrations of meperidine. The proposed mechanism is cimetidine inhibition of meperidine metabolism via hepatic microsomal enzymes.', 'DDInter', 'Caution is advised if meperidine must be used with cimetidine. Patients should be closely monitored for potentially excessive or prolonged central nervous system and respiratory depression. Ambulatory patients should be made aware of the possibility of additive CNS effects (e.g., drowsiness, dizziness, lightheadedness, confusion) and counseled to avoid activities requiring mental alertness until they know how these agents affect them. Patients should also be advised to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities. Alternatively, use of another H2-antagonist such as famotidine, nizatidine, or ranitidine may be considered, as these agents have not been shown to significantly affect CYP450 metabolism.', 'Metabolism', 'Caution is advised if meperidine must be used with cimetidine.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/65660/', '', 'Naloxone, Naltrexone, Tapentadol', 'Tinidazole, Levofloxacin, Tetracycline, Dexlansoprazole, Bismuth subcitrate potassium, Rifabutin, Nizatidine, Esomeprazole, Vonoprazan, Amoxicillin', 1767369485);
